using LibraryManagement.controller;
using LibraryManagement.layout.Book.Forms;
using LibraryManagement.model;
using LibraryManagement.model.Test;
using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Threading.Tasks;
using System.Windows;
using System.Windows.Input;

namespace LibraryManagement.viewmodel.Books
{
    public class AddNewBookViewModel: BaseViewModel
    {
        private DataLoadFromDB data;
        public static event updateBookListHandeler update;

        public string name { get; set; }
        public string price { get; set; }
        public string author { get; set; }
        public DateTime? publishingDate { get; set; }
      
        public AddNewBookViewModel()
        {
            data = DataLoadFromDB.getIntance();
            name = "";
            price = "";
            author = "";
            publishingDate = null;
        }
        public AddNewBookViewModel(string test)
        {
            name = "";
            price = "";
            author = "";
            publishingDate = null;
        }
        private static readonly Regex _regex = new Regex("[^0-9-]+"); //regex that matches disallowed text
        private bool IsTextAllowed(string text)
        {
            return !_regex.IsMatch(text);
        }
        public bool onButtonSaveClick(string price,string name, string author, DateTime? publishingDate, string imageName, int numOfCopies)
        {
            if (price != "" && name != "" && author != "" && publishingDate != null)
            {
                if (IsTextAllowed(price) && Double.Parse(price) > 1000 && Double.Parse(price) < 1000000000)
                {
                    if (publishingDate <= DateTime.Now.Date)
                    {
                        model.Book newBook = new model.Book(name, author, (DateTime)publishingDate, Double.Parse(price));
                        if (imageName != "")
                        {
                            byte[] imageData = File.ReadAllBytes(imageName);
                            string base64String = Convert.ToBase64String(imageData, 0, imageData.Length);
                            newBook.imgSource = base64String;
                        }
                        else
                        {
                            newBook.imgSource = "";
                        }
                        data.addNewBook(newBook);                        
                        int autoID = newBook.id * 1000;
                        for (int i = 0; i < numOfCopies; i++)
                        {
                            autoID += 1;
                            data.addNewBookItem(new LibraryManagement.model.BookItem(autoID, newBook.id, model.enums.LendingStatus.AVAI));
                        }
                        AddBookSuccessForm add = new AddBookSuccessForm(newBook);
                        add.Show();
                        update();
                        return true;
                    }
                    else
                    {
                        MessageBox.Show("Pulishing date is in future!!", "Error", MessageBoxButton.OK, MessageBoxImage.Error);
                        return false;
                    }
                }
                else
                {
                    MessageBox.Show("Price must be a number between 1.000đ - 1.000.000.000đ", "Error", MessageBoxButton.OK, MessageBoxImage.Error);
                    return false;
                }
            }
            else
            {
                MessageBox.Show("Some field is empy", "Error", MessageBoxButton.OK, MessageBoxImage.Error);
                return false;
            }
        }

        public AddNewBookResult onButtonSaveClickTest(string price, string name, string author, DateTime? publishingDate, string imageName, int numOfCopies)
        {
            if (price != "" && name != "" && author != "" && publishingDate != null)
            {
                if (IsTextAllowed(price) && Double.Parse(price) > 1000 && Double.Parse(price) < 1000000000)
                {
                    if (publishingDate <= DateTime.Now.Date)
                    {
                        AddNewBookResult addNewBookResult = new AddNewBookResult(true, false, false, "");
                        model.Book newBook = new model.Book(name, author, (DateTime)publishingDate, Double.Parse(price), 99).buildWithID(13);                        
                        addNewBookResult.isAddBookInDatabase = true;
                        int autoID = newBook.id * 1000;                        
                        for (int i = 0; i < numOfCopies; i++)
                        {
                            autoID += 1;
                            addNewBookResult.isAddBookItemInDatabase = true;
                        }                       
                        return addNewBookResult;
                    }
                    else
                    {                       
                        return new AddNewBookResult(false, false, false, "Pulishing date is in future!!") ;
                    }
                }
                else
                {                    
                    return new AddNewBookResult(false, false, false, "Price must be a number between 1.000đ - 1.000.000.000đ");
                }
            }
            else
            {                
                return new AddNewBookResult(false, false, false, "Some field is empty");
            }
        }
    }
}
